import React, {useState, useEffect} from "react";

import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js'
import {Bar} from 'react-chartjs-2'
import axios from "axios";

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
)

function getAPIData() {
    return axios.get('http://localhost:3000/daily_sum').then((response) => response.data)
}

const BarChart = () => {




    const [charData, setCharData] = useState({
        datasets: [],
    })


    const [chartOptions, setChartOptions] = useState({})

    useEffect(() => {
        setCharData({
            labels: ["John", "Kevin", "George", "M", "oreo"],
            datasets: [{
                label: 'Whomst let the dogs out',
                data: [12, 55, 34, 120, 720],
                borderColor: 'rgb(53, 162, 235)',
                backgroundColor: 'rgba(53, 162, 253, 0.4)'
            },
        ],
        });
        setChartOptions({
            responsive: true,
            plugins: {
                legend: {
                    position: 'top'
                },
                title: {
                    display: true,
                    text: "W"
            }
        }
        })
    }, [])

    return <div>
        <Bar options={chartOptions} data={charData}/>
    </div>
}

export default BarChart
